import{_ as e,c as o,o as a,ag as r}from"./chunks/framework.B2cFWCz1.js";const g=JSON.parse('{"title":"Typography Options","description":"","frontmatter":{},"headers":[],"relativePath":"wordpress/controls/typography-options.md","filePath":"wordpress/controls/typography-options.md"}'),i={name:"wordpress/controls/typography-options.md"};function l(n,t,d,s,h,p){return a(),o("div",null,t[0]||(t[0]=[r('<h1 id="typography-options" tabindex="-1">Typography Options <a class="header-anchor" href="#typography-options" aria-label="Permalink to &quot;Typography Options&quot;">​</a></h1><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>The Typography Options control in the Phenix Design System provides comprehensive text styling capabilities for blocks that contain text content. Available through the Typography button in the block toolbar, these controls allow you to customize every aspect of text appearance without writing custom CSS. With responsive options for different screen sizes, you can ensure your typography looks perfect on all devices.</p><h2 id="key-features" tabindex="-1">Key Features <a class="header-anchor" href="#key-features" aria-label="Permalink to &quot;Key Features&quot;">​</a></h2><ul><li><strong>Font family selection</strong> from your theme&#39;s available fonts</li><li><strong>Font size control</strong> with responsive options</li><li><strong>Font weight adjustment</strong> for text emphasis</li><li><strong>Line height settings</strong> for optimal readability</li><li><strong>Text alignment options</strong> for perfect positioning</li><li><strong>Text decoration controls</strong> for underlining, strikethrough, etc.</li><li><strong>Text transformation</strong> for case styling (uppercase, lowercase, etc.)</li><li><strong>Letter spacing adjustment</strong> for fine-tuning character spacing</li><li><strong>Text shadow effects</strong> for depth and emphasis</li><li><strong>Text color selection</strong> from theme palette or custom colors</li><li><strong>Responsive controls</strong> for all typography settings</li></ul><h2 id="accessing-typography-controls" tabindex="-1">Accessing Typography Controls <a class="header-anchor" href="#accessing-typography-controls" aria-label="Permalink to &quot;Accessing Typography Controls&quot;">​</a></h2><p>The Typography Options are available for any block that contains text content:</p><ol><li>Select a block with text content</li><li>Look for the Typography button (&quot;T&quot; icon) in the block toolbar</li><li>Click the Typography button to open the typography controls panel</li></ol><h2 id="available-controls" tabindex="-1">Available Controls <a class="header-anchor" href="#available-controls" aria-label="Permalink to &quot;Available Controls&quot;">​</a></h2><h3 id="font-family" tabindex="-1">Font Family <a class="header-anchor" href="#font-family" aria-label="Permalink to &quot;Font Family&quot;">​</a></h3><p>Select from the available font families defined in your theme:</p><ul><li>System fonts (default)</li><li>Google Fonts (if configured in theme)</li><li>Custom fonts (if added to your theme)</li></ul><h3 id="font-size" tabindex="-1">Font Size <a class="header-anchor" href="#font-size" aria-label="Permalink to &quot;Font Size&quot;">​</a></h3><p>Control the size of your text with preset options or custom values:</p><table tabindex="0"><thead><tr><th>Preset</th><th>Value</th><th>Description</th></tr></thead><tbody><tr><td>XS</td><td>0.75rem (12px)</td><td>Extra small text</td></tr><tr><td>SM</td><td>0.875rem (14px)</td><td>Small text</td></tr><tr><td>Normal</td><td>1rem (16px)</td><td>Default text size</td></tr><tr><td>MD</td><td>1.125rem (18px)</td><td>Medium text</td></tr><tr><td>LG</td><td>1.25rem (20px)</td><td>Large text</td></tr><tr><td>XL</td><td>1.5rem (24px)</td><td>Extra large text</td></tr><tr><td>XXL</td><td>2rem (32px)</td><td>Double extra large text</td></tr><tr><td>Custom</td><td>Any value</td><td>Custom size with units (px, rem, em, etc.)</td></tr></tbody></table><h3 id="font-weight" tabindex="-1">Font Weight <a class="header-anchor" href="#font-weight" aria-label="Permalink to &quot;Font Weight&quot;">​</a></h3><p>Adjust the thickness of your text:</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>Light</td><td>300 weight</td></tr><tr><td>Regular</td><td>400 weight (default)</td></tr><tr><td>Medium</td><td>500 weight</td></tr><tr><td>Semi-Bold</td><td>600 weight</td></tr><tr><td>Bold</td><td>700 weight</td></tr><tr><td>Extra-Bold</td><td>800 weight</td></tr><tr><td>Black</td><td>900 weight</td></tr></tbody></table><h3 id="line-height" tabindex="-1">Line Height <a class="header-anchor" href="#line-height" aria-label="Permalink to &quot;Line Height&quot;">​</a></h3><p>Control the vertical spacing between lines of text:</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>Default</td><td>Browser default (usually about 1.2)</td></tr><tr><td>Tight</td><td>1.25 - Compact line spacing</td></tr><tr><td>Normal</td><td>1.5 - Standard readable spacing</td></tr><tr><td>Relaxed</td><td>1.75 - More generous spacing</td></tr><tr><td>Loose</td><td>2 - Maximum spacing for readability</td></tr><tr><td>Custom</td><td>Any value (unitless or with units)</td></tr></tbody></table><h3 id="text-alignment" tabindex="-1">Text Alignment <a class="header-anchor" href="#text-alignment" aria-label="Permalink to &quot;Text Alignment&quot;">​</a></h3><p>Control how text aligns horizontally:</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>Left</td><td>Align text to the left</td></tr><tr><td>Center</td><td>Center text horizontally</td></tr><tr><td>Right</td><td>Align text to the right</td></tr><tr><td>Justify</td><td>Stretch text to fill the width (except last line)</td></tr></tbody></table><h3 id="text-decoration" tabindex="-1">Text Decoration <a class="header-anchor" href="#text-decoration" aria-label="Permalink to &quot;Text Decoration&quot;">​</a></h3><p>Add lines to your text:</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>None</td><td>No decoration (default)</td></tr><tr><td>Underline</td><td>Line below the text</td></tr><tr><td>Overline</td><td>Line above the text</td></tr><tr><td>Line-through</td><td>Line through the middle of the text</td></tr></tbody></table><h3 id="text-transform" tabindex="-1">Text Transform <a class="header-anchor" href="#text-transform" aria-label="Permalink to &quot;Text Transform&quot;">​</a></h3><p>Control the capitalization of your text:</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>None</td><td>No transformation (default)</td></tr><tr><td>Uppercase</td><td>ALL CAPITAL LETTERS</td></tr><tr><td>Lowercase</td><td>all small letters</td></tr><tr><td>Capitalize</td><td>First Letter Of Each Word Capitalized</td></tr></tbody></table><h3 id="letter-spacing" tabindex="-1">Letter Spacing <a class="header-anchor" href="#letter-spacing" aria-label="Permalink to &quot;Letter Spacing&quot;">​</a></h3><p>Adjust the space between characters:</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>Normal</td><td>Default spacing</td></tr><tr><td>Tight</td><td>-0.05em (characters closer together)</td></tr><tr><td>Wide</td><td>0.05em (characters further apart)</td></tr><tr><td>Wider</td><td>0.1em (maximum spacing)</td></tr><tr><td>Custom</td><td>Any value with units (em, px, etc.)</td></tr></tbody></table><h3 id="text-shadow" tabindex="-1">Text Shadow <a class="header-anchor" href="#text-shadow" aria-label="Permalink to &quot;Text Shadow&quot;">​</a></h3><p>Add shadow effects to your text for depth and emphasis:</p><ul><li>Horizontal Offset: Control shadow position left/right</li><li>Vertical Offset: Control shadow position up/down</li><li>Blur Radius: Control shadow softness</li><li>Shadow Color: Select color with opacity</li></ul><h3 id="text-color" tabindex="-1">Text Color <a class="header-anchor" href="#text-color" aria-label="Permalink to &quot;Text Color&quot;">​</a></h3><p>Choose text colors from:</p><ul><li>Theme color palette</li><li>Custom color picker</li><li>Color opacity/transparency control</li></ul><h2 id="responsive-typography" tabindex="-1">Responsive Typography <a class="header-anchor" href="#responsive-typography" aria-label="Permalink to &quot;Responsive Typography&quot;">​</a></h2><p>All typography options can be set differently for various screen sizes:</p><table tabindex="0"><thead><tr><th>Breakpoint</th><th>Description</th></tr></thead><tbody><tr><td>Default</td><td>Applied to all screen sizes unless overridden</td></tr><tr><td>Mobile</td><td>Applied to screens smaller than 768px</td></tr><tr><td>Tablet</td><td>Applied to screens 768px-991px</td></tr><tr><td>Desktop</td><td>Applied to screens 992px-1199px</td></tr><tr><td>Large Desktop</td><td>Applied to screens 1200px and larger</td></tr></tbody></table><p>To set responsive typography:</p><ol><li>Open the Typography controls panel</li><li>Click the responsive toggle button to switch between breakpoints</li><li>Set the desired typography options for each breakpoint</li></ol><h2 id="usage-examples" tabindex="-1">Usage Examples <a class="header-anchor" href="#usage-examples" aria-label="Permalink to &quot;Usage Examples&quot;">​</a></h2><h3 id="creating-a-responsive-heading" tabindex="-1">Creating a Responsive Heading <a class="header-anchor" href="#creating-a-responsive-heading" aria-label="Permalink to &quot;Creating a Responsive Heading&quot;">​</a></h3><ol><li>Select a Heading block</li><li>Open the Typography controls</li><li>For default view: <ul><li>Set Font Size to &quot;XXL&quot; (2rem/32px)</li><li>Set Font Weight to &quot;Bold&quot;</li></ul></li><li>For mobile view: <ul><li>Switch to Mobile breakpoint</li><li>Set Font Size to &quot;XL&quot; (1.5rem/24px)</li></ul></li></ol><h3 id="styling-a-call-to-action-paragraph" tabindex="-1">Styling a Call-to-Action Paragraph <a class="header-anchor" href="#styling-a-call-to-action-paragraph" aria-label="Permalink to &quot;Styling a Call-to-Action Paragraph&quot;">​</a></h3><ol><li>Select a Paragraph block</li><li>Open the Typography controls</li><li>Set Font Size to &quot;MD&quot; (1.125rem/18px)</li><li>Set Font Weight to &quot;Medium&quot;</li><li>Set Text Alignment to &quot;Center&quot;</li><li>Add a subtle Text Shadow for emphasis</li></ol><h3 id="creating-highlighted-quote-text" tabindex="-1">Creating Highlighted Quote Text <a class="header-anchor" href="#creating-highlighted-quote-text" aria-label="Permalink to &quot;Creating Highlighted Quote Text&quot;">​</a></h3><ol><li>Select a Quote block</li><li>Open the Typography controls</li><li>Set Font Style to &quot;Italic&quot;</li><li>Set Font Size to &quot;LG&quot; (1.25rem/20px)</li><li>Set Line Height to &quot;Relaxed&quot;</li><li>Set Letter Spacing to &quot;Wide&quot;</li></ol><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><ol><li><p><strong>Maintain hierarchy</strong>: Use size and weight to create clear visual hierarchy</p></li><li><p><strong>Limit font families</strong>: Use no more than 2-3 font families per page</p></li><li><p><strong>Consider readability</strong>: Ensure text is readable at all sizes and on all devices</p></li><li><p><strong>Use responsive typography</strong>: Adjust text sizes for different screen sizes</p></li><li><p><strong>Be consistent</strong>: Use the same typography styles for similar elements</p></li><li><p><strong>Consider contrast</strong>: Ensure sufficient contrast between text and background</p></li><li><p><strong>Use system fonts for performance</strong>: System fonts load faster than web fonts</p></li></ol><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Issue</th><th>Solution</th></tr></thead><tbody><tr><td>Font not displaying correctly</td><td>Check if the font is properly loaded in your theme</td></tr><tr><td>Text appears cut off</td><td>Increase line height or container size</td></tr><tr><td>Inconsistent text size across devices</td><td>Set specific font sizes for each responsive breakpoint</td></tr><tr><td>Text shadow not visible</td><td>Ensure shadow color has sufficient contrast with background</td></tr><tr><td>Justified text creating large gaps</td><td>Add hyphens or adjust container width</td></tr></tbody></table><h2 id="related-controls" tabindex="-1">Related Controls <a class="header-anchor" href="#related-controls" aria-label="Permalink to &quot;Related Controls&quot;">​</a></h2><ul><li><a href="./styles-options.html">Styles Options</a></li><li><a href="./layout-options.html">Layout Options</a></li><li><a href="./responsive-margin.html">Responsive Margin</a></li><li><a href="./responsive-padding.html">Responsive Padding</a></li></ul>',57)]))}const u=e(i,[["render",l]]);export{g as __pageData,u as default};
