import{_ as t,c as i,o as n,ag as o}from"./chunks/framework.DPDPlp3K.js";const m=JSON.parse('{"title":"Phenix Design System","description":"","frontmatter":{"layout":"home","title":"Phenix Design System","hero":{"name":"Phenix Design System","text":"A powerful, modular design system for WordPress and beyond","tagline":"Build beautiful, responsive websites faster with a comprehensive design system that prioritizes simplicity, performance, and flexibility.","image":{"src":"/logo.png","alt":"Phenix Design System"},"actions":[{"theme":"brand","text":"Get Started","link":"/frontend/"},{"theme":"alt","text":"View on GitHub","link":"https://github.com/EngCode/phenix-blocks"}]}},"headers":[],"relativePath":"index.md","filePath":"index.md"}'),s={name:"index.md"};function r(a,e,l,d,h,c){return n(),i("div",null,e[0]||(e[0]=[o('<div style="padding:2rem;"></div><h1 id="welcome-to-phenix-design-system" tabindex="-1">Welcome to Phenix Design System <a class="header-anchor" href="#welcome-to-phenix-design-system" aria-label="Permalink to &quot;Welcome to Phenix Design System&quot;">​</a></h1><p>Phenix is a comprehensive design system that provides a complete foundation for building modern, responsive websites. Originally developed for WordPress, the core system can be used for any web project, providing consistent UI components, utilities, and development patterns.</p><h2 id="what-is-phenix-design-system" tabindex="-1">What is Phenix Design System? <a class="header-anchor" href="#what-is-phenix-design-system" aria-label="Permalink to &quot;What is Phenix Design System?&quot;">​</a></h2><p>Phenix Design System is a modular, component-based design framework that helps developers and designers create consistent, beautiful interfaces. It includes modules covering:</p><ul><li>SASS Styling &amp; Framework</li><li>TypeScript Utilities &amp; Components</li><li>WordPress Integration (including Gutenberg Blocks)</li><li>Developer Tools &amp; Utilities</li></ul><h2 id="designed-for-everyone" tabindex="-1">Designed for Everyone <a class="header-anchor" href="#designed-for-everyone" aria-label="Permalink to &quot;Designed for Everyone&quot;">​</a></h2><p>Whether you&#39;re a professional user, frontend developer, or backend developer, Phenix Design System offers tools that make your work easier:</p><ul><li><strong>For Professional Users</strong>: An intuitive interface to create websites without coding</li><li><strong>For Frontend Developers</strong>: A flexible framework that works with any modern stack</li><li><strong>For Backend Developers</strong>: WordPress integration and PHP utilities</li></ul><h2 id="core-principles" tabindex="-1">Core Principles <a class="header-anchor" href="#core-principles" aria-label="Permalink to &quot;Core Principles&quot;">​</a></h2><p>The Phenix Design System is built on these core principles:</p><ol><li><strong>Simplicity</strong>: Clean, readable code that&#39;s easy to understand and maintain</li><li><strong>Flexibility</strong>: Adaptable components that work in various contexts</li><li><strong>Performance</strong>: Optimized code that loads quickly and runs efficiently</li><li><strong>Consistency</strong>: Unified design language across all components</li></ol><h2 id="future-roadmap" tabindex="-1">Future Roadmap <a class="header-anchor" href="#future-roadmap" aria-label="Permalink to &quot;Future Roadmap&quot;">​</a></h2><p>The Phenix Design System continues to evolve with plans to integrate:</p><ul><li>GSAP animation features for advanced motion design</li><li>Three.js integration for 3D elements</li><li>Enhanced WordPress tools and Gutenberg blocks</li></ul><h2 id="get-started" tabindex="-1">Get Started <a class="header-anchor" href="#get-started" aria-label="Permalink to &quot;Get Started&quot;">​</a></h2><p>Ready to dive in? Start by exploring our documentation using the sidebar navigation, beginning with the <a href="/frontend/">Front-End Overview</a> section. You can also check out our <a href="https://github.com/EngCode/phenix-blocks" target="_blank" rel="noreferrer">GitHub repository</a> to view the source code.</p>',17)]))}const g=t(s,[["render",r]]);export{m as __pageData,g as default};
