import{_ as t,c as o,o as i,ag as s}from"./chunks/framework.B2cFWCz1.js";const m=JSON.parse('{"title":"Phenix Design System","description":"","frontmatter":{"layout":"home","title":"Phenix Design System","hero":{"name":"Phenix Design System","text":"The Ultimate WordPress Block Editor Enhancement","tagline":"A comprehensive design system that transforms WordPress into a powerful website builder with advanced blocks, styling controls, and developer tools.","actions":[{"theme":"brand","text":"Front-End Framework","link":"/frontend/"},{"theme":"brand","text":"WordPress Integration","link":"/wordpress/"},{"theme":"alt","text":"GitHub","link":"https://github.com/EngCode/phenix-blocks"}]}},"headers":[],"relativePath":"index.md","filePath":"index.md"}'),n={name:"index.md"};function r(a,e,l,d,c,g){return i(),o("div",null,e[0]||(e[0]=[s('<div style="padding:2rem;"></div><h1 id="welcome-to-phenix-design-system" tabindex="-1">Welcome to Phenix Design System <a class="header-anchor" href="#welcome-to-phenix-design-system" aria-label="Permalink to &quot;Welcome to Phenix Design System&quot;">​</a></h1><p>Phenix Design System is a powerful WordPress plugin (version 1.3.0) that enhances the block editor with a comprehensive set of custom blocks, advanced styling controls, and developer tools. It combines a robust front-end framework with seamless WordPress integration to provide everything needed for building modern, responsive websites without coding. The latest version introduces an advanced animation system with scroll-driven animations and 3D interactive media embedding capabilities.</p><h2 id="what-is-phenix-blocks" tabindex="-1">What is Phenix Blocks? <a class="header-anchor" href="#what-is-phenix-blocks" aria-label="Permalink to &quot;What is Phenix Blocks?&quot;">​</a></h2><p>Phenix Blocks (<code>pds-blocks</code>) is a complete design ecosystem for WordPress that includes:</p><ul><li><strong>Custom Gutenberg Blocks</strong>: A collection of 20+ specialized blocks for creating complex layouts and dynamic content</li><li><strong>Advanced Animation System</strong>: Scroll-driven animations with progress tracking and exit animations</li><li><strong>3D Interactive Media</strong>: Embed and control interactive 3D models with Three.js integration</li><li><strong>Integrated Third-Party Controls</strong>: Slider, typewriter, parallax effects, and more specialized components</li><li><strong>Advanced Block Controls</strong>: Extensive styling and layout options with responsive capabilities</li><li><strong>Theme Customization</strong>: Modular SASS structure, JavaScript integration, and dynamic template parts</li><li><strong>Developer Tools</strong>: Built-in APIs and utilities for creating custom post types, taxonomies, meta boxes, and more</li><li><strong>Performance Optimization</strong>: Comprehensive tools to enhance site speed and user experience</li><li><strong>WooCommerce Integration</strong>: Special features and optimizations for e-commerce sites</li><li><strong>Multi-language Support</strong>: Full RTL compatibility and translation readiness</li></ul><h2 id="custom-gutenberg-blocks" tabindex="-1">Custom Gutenberg Blocks <a class="header-anchor" href="#custom-gutenberg-blocks" aria-label="Permalink to &quot;Custom Gutenberg Blocks&quot;">​</a></h2><div class="blocks-grid"><div class="block-category"><h3>Layout Blocks</h3><ul><li><strong>Container</strong>: Create boxed or full-width content areas with advanced styling options</li><li><strong>Grid Row</strong>: Flexible row layouts with responsive controls and gap settings</li><li><strong>Grid Column</strong>: Responsive columns with width controls for all device sizes</li><li><strong>Group</strong>: Group content with advanced styling and positioning options</li><li><strong>Theme Part</strong>: Include reusable template parts within your content</li></ul></div><div class="block-category"><h3>Content Blocks</h3><ul><li><strong>Text</strong>: Enhanced text elements with advanced typography controls</li><li><strong>Media Element</strong>: Advanced image and video handling with responsive controls</li><li><strong>Icon Element</strong>: Insert and customize icons from multiple icon libraries</li><li><strong>Button</strong>: Create buttons with extensive styling and interaction options</li><li><strong>Logo</strong>: Insert and customize your site logo with sizing controls</li><li><strong>Page Head</strong>: Create dynamic page headers with title and breadcrumb support</li></ul></div><div class="block-category"><h3>Interactive Blocks</h3><ul><li><strong>Popup</strong>: Create modal popups with custom triggers and animations</li><li><strong>Navigation</strong>: Build responsive menus with mobile support and custom styling</li><li><strong>Custom Code</strong>: Insert custom HTML, CSS, and JavaScript</li><li><strong>Logical Content</strong>: Display conditional content based on user roles, devices, or languages</li></ul></div><div class="block-category"><h3>Dynamic Blocks</h3><ul><li><strong>Query</strong>: Display posts with advanced filtering and pagination options</li><li><strong>Taxonomies</strong>: Show categories, tags, or custom taxonomies with styling options</li><li><strong>Users Query</strong>: Display user information with filtering and styling controls</li></ul></div></div><h2 id="advanced-animation-system" tabindex="-1">Advanced Animation System <a class="header-anchor" href="#advanced-animation-system" aria-label="Permalink to &quot;Advanced Animation System&quot;">​</a></h2><p>The latest version introduces a powerful animation system that takes your content to the next level:</p><div class="features-grid"><div class="feature-category"><h3>Scroll-Driven Animations</h3><ul><li><strong>Progress Tracking</strong>: Control animation progress based on scroll position</li><li><strong>Viewport Detection</strong>: Trigger animations when elements enter or exit the viewport</li><li><strong>Exit Animations</strong>: Define separate animations when elements leave the viewport</li><li><strong>Animation Delay</strong>: Set staggered timing for groups of animated elements</li></ul></div><div class="feature-category"><h3>3D Interactive Media</h3><ul><li><strong>Model Viewer</strong>: Display interactive 3D models with pan, zoom, and rotate controls</li><li><strong>Multiple Format Support</strong>: Support for GLTF, GLB, OBJ, and FBX formats</li><li><strong>Responsive Sizing</strong>: Automatically adapt 3D content to different screen sizes</li><li><strong>Three.js Integration</strong>: Powered by the industry-standard 3D library</li></ul></div></div><h2 id="integrated-third-party-controls" tabindex="-1">Integrated Third-Party Controls <a class="header-anchor" href="#integrated-third-party-controls" aria-label="Permalink to &quot;Integrated Third-Party Controls&quot;">​</a></h2><p>Phenix Design System seamlessly integrates powerful third-party components to enhance your website&#39;s functionality:</p><div class="features-grid"><div class="feature-category"><h3>Interactive Elements</h3><ul><li><strong>Swiper Slider</strong>: Advanced touch-enabled slider with multiple configuration options</li><li><strong>Typewriter Effect</strong>: Create dynamic typing animations with customizable speed and text</li><li><strong>Parallax Effects</strong>: Create depth with elements that move at different speeds while scrolling</li><li><strong>Lightbox Gallery</strong>: Interactive image and video galleries with zoom and navigation</li></ul></div><div class="feature-category"><h3>Layout &amp; Display</h3><ul><li><strong>Masonry Layout</strong>: Dynamic grid layouts for varied content sizes with no gaps</li><li><strong>Accordion Panels</strong>: Collapsible content sections for better space utilization</li><li><strong>Tabs Interface</strong>: Organize content into tabbed sections for better user experience</li><li><strong>Advanced Data Tables</strong>: Sortable and filterable tables for data presentation</li></ul></div></div><h2 id="advanced-block-controls" tabindex="-1">Advanced Block Controls <a class="header-anchor" href="#advanced-block-controls" aria-label="Permalink to &quot;Advanced Block Controls&quot;">​</a></h2><div class="controls-grid"><div class="control-category"><h3>Layout Controls</h3><p>Fine-tune the structure and positioning of your content:</p><ul><li><strong>Alignment</strong>: Control horizontal and vertical alignment</li><li><strong>Dimensions</strong>: Set width, height, and max-width/height</li><li><strong>Spacing</strong>: Control margin and padding with responsive options</li><li><strong>Position</strong>: Set position type and coordinates (absolute, relative, etc.)</li><li><strong>Display</strong>: Control how elements display on different devices</li></ul></div><div class="control-category"><h3>Style Controls</h3><p>Customize the visual appearance of your blocks:</p><ul><li><strong>Colors</strong>: Set text, background, and border colors</li><li><strong>Typography</strong>: Control font family, size, weight, and style</li><li><strong>Borders</strong>: Add and style borders with width, style, and radius options</li><li><strong>Effects</strong>: Add shadows, overlays, and hover effects</li><li><strong>Animations</strong>: Add entrance and hover animations</li></ul></div><div class="control-category"><h3>Responsive Controls</h3><p>Ensure your design looks great on all devices:</p><ul><li><strong>Device-specific Settings</strong>: Customize for desktop, tablet, and mobile</li><li><strong>Responsive Display</strong>: Show/hide elements based on screen size</li><li><strong>Responsive Typography</strong>: Adjust text size for different devices</li><li><strong>Responsive Spacing</strong>: Control margin and padding per device</li></ul></div><div class="control-category"><h3>Advanced Options</h3><p>Take full control of your blocks:</p><ul><li><strong>Custom CSS</strong>: Add custom CSS classes and inline styles</li><li><strong>Custom Attributes</strong>: Add data attributes and custom HTML attributes</li><li><strong>Motion Effects</strong>: Add scroll animations and parallax effects</li><li><strong>Conditional Logic</strong>: Display content based on user roles or device types</li></ul></div></div><h2 id="developer-tools-optimization" tabindex="-1">Developer Tools &amp; Optimization <a class="header-anchor" href="#developer-tools-optimization" aria-label="Permalink to &quot;Developer Tools &amp; Optimization&quot;">​</a></h2><div class="tools-grid"><div class="tool-category"><h3>WordPress Extenders</h3><p>Powerful tools to extend WordPress functionality:</p><ul><li><strong>Custom Post Types Creator</strong>: Build custom content types with full UI</li><li><strong>Custom Taxonomies Creator</strong>: Create custom categories and tags</li><li><strong>Meta Box Creator</strong>: Add custom fields to any post type</li><li><strong>Block Patterns Creator</strong>: Build and register reusable patterns</li><li><strong>Menu Locations</strong>: Register custom menu positions</li><li><strong>Admin Page Creator</strong>: Build custom admin interfaces</li><li><strong>API Endpoint Creator</strong>: Create custom REST API endpoints</li></ul></div><div class="tool-category"><h3>Performance Optimization</h3><p>Comprehensive tools to enhance site speed:</p><ul><li><strong>Head Optimizer</strong>: Remove unnecessary WordPress scripts</li><li><strong>Scripts Optimizer</strong>: Control and optimize JavaScript loading</li><li><strong>Styles Optimizer</strong>: Control and optimize CSS loading</li><li><strong>Emoji Remover</strong>: Remove WordPress emoji scripts</li><li><strong>Image Optimization</strong>: Control image sizes and loading</li><li><strong>Third-party Optimization</strong>: Optimize external scripts</li><li><strong>WooCommerce Optimizer</strong>: Enhance WooCommerce performance</li></ul></div><div class="tool-category"><h3>WooCommerce Integration</h3><p>Enhanced e-commerce capabilities:</p><ul><li><strong>WooCommerce Optimizer</strong>: Remove unnecessary scripts and styles</li><li><strong>Custom Cart Fragment</strong>: Optimized AJAX cart updates</li><li><strong>Custom Mini Cart</strong>: Enhanced mini cart functionality</li><li><strong>Product Display</strong>: Improved product layouts and features</li><li><strong>Custom Sorting</strong>: Enhanced product sorting options</li></ul></div><div class="tool-category"><h3>User Experience</h3><p>Features to enhance the user experience:</p><ul><li><strong>Loading Screen</strong>: Customizable pre-loading animations</li><li><strong>Login Customization</strong>: Brand the WordPress login page</li><li><strong>Lightbox</strong>: Enhanced image and media viewing</li><li><strong>Admin Customization</strong>: Branded admin experience</li></ul></div></div><h2 id="front-end-framework" tabindex="-1">Front-End Framework <a class="header-anchor" href="#front-end-framework" aria-label="Permalink to &quot;Front-End Framework&quot;">​</a></h2><div class="framework-grid"><div class="framework-feature"><h3>CSS Framework</h3><p>A lightweight yet powerful CSS framework:</p><ul><li><strong>Flexible Grid System</strong>: Responsive layouts with flexbox</li><li><strong>Typography System</strong>: Comprehensive text styling</li><li><strong>Utility Classes</strong>: Extensive collection for rapid styling</li><li><strong>Component Library</strong>: Pre-built UI components</li><li><strong>Responsive Design</strong>: Mobile-first approach with 5 breakpoints</li><li><strong>RTL Support</strong>: Full bidirectional text support</li></ul></div><div class="framework-feature"><h3>JavaScript Utilities</h3><p>Powerful JavaScript tools and components:</p><ul><li><strong>DOM Manipulation</strong>: Simplified element handling</li><li><strong>UI Effects</strong>: Animations, transitions, and interactions</li><li><strong>Form Handling</strong>: Validation and advanced inputs</li><li><strong>Component Controllers</strong>: For tabs, accordions, modals, etc.</li><li><strong>Media Handling</strong>: Image, video, and audio utilities</li></ul></div></div><h2 id="core-principles-benefits" tabindex="-1">Core Principles &amp; Benefits <a class="header-anchor" href="#core-principles-benefits" aria-label="Permalink to &quot;Core Principles &amp; Benefits&quot;">​</a></h2><div class="principles-grid"><div class="principle"><h3>No-Code Website Building</h3><p>Create complex, professional websites without writing a single line of code. The intuitive block editor and pre-designed patterns make it easy for anyone to build beautiful pages.</p></div><div class="principle"><h3>Developer-Friendly Architecture</h3><p>Built with modern development practices using SASS and TypeScript. The modular structure and comprehensive APIs make it easy to extend and customize.</p></div><div class="principle"><h3>Performance-Focused</h3><p>Optimized for speed with minimal file sizes (Core CSS: 140KB/32KB gzipped, JS: 80KB/30KB gzipped) and built-in performance enhancement tools to ensure fast-loading websites.</p></div><div class="principle"><h3>Comprehensive Design System</h3><p>A complete design ecosystem with consistent styling, typography, spacing, and interaction patterns across all components for a cohesive user experience.</p></div></div><h2 id="who-it-s-for" tabindex="-1">Who It&#39;s For <a class="header-anchor" href="#who-it-s-for" aria-label="Permalink to &quot;Who It&#39;s For&quot;">​</a></h2><div class="audience-grid"><div class="audience"><h3>Content Creators &amp; Site Editors</h3><p>Build professional websites without coding knowledge. The intuitive block editor and pre-designed patterns make it easy to create beautiful, responsive layouts.</p><a href="/wordpress/getting-started/installation" class="btn">Start Creating →</a></div><div class="audience"><h3>WordPress Developers</h3><p>Extend WordPress with powerful developer tools. Create custom post types, taxonomies, meta boxes, and API endpoints with minimal code.</p><a href="/wordpress/dev-tools/block-patterns-templates" class="btn">Start Developing →</a></div><div class="audience"><h3>Theme Developers</h3><p>Build powerful themes with the Phenix framework. The block editor integration and styling system provide a solid foundation for custom themes.</p><a href="/frontend/getting-started/quick-start/installation" class="btn">Explore Framework →</a></div><div class="audience"><h3>Agency Owners</h3><p>Streamline your workflow and deliver client projects faster. The comprehensive block library and styling options enable rapid development of custom websites.</p><a href="/wordpress/blocks/container-block" class="btn">See Blocks →</a></div></div><h2 id="technical-specifications" tabindex="-1">Technical Specifications <a class="header-anchor" href="#technical-specifications" aria-label="Permalink to &quot;Technical Specifications&quot;">​</a></h2><ul><li><strong>Version</strong>: 1.3.0</li><li><strong>Requirements</strong>: WordPress 6.0+, PHP 7.4+</li><li><strong>Browser Support</strong>: Chrome, Firefox, Safari, Edge (latest versions)</li><li><strong>File Size</strong>: Core CSS (140KB/32KB gzipped), JavaScript (80KB/30KB gzipped)</li><li><strong>Languages</strong>: Full RTL support and translation-ready</li><li><strong>License</strong>: GPL2</li></ul><h2 id="documentation" tabindex="-1">Documentation <a class="header-anchor" href="#documentation" aria-label="Permalink to &quot;Documentation&quot;">​</a></h2><div class="docs-grid"><div class="docs-section"><h3>Getting Started</h3><ul><li><a href="/wordpress/getting-started/installation">Installation Guide</a></li><li><a href="/wordpress/getting-started/general-settings">General Settings</a></li><li><a href="/wordpress/getting-started/theme-customizing">Theme Customization</a></li></ul></div><div class="docs-section"><h3>Blocks Library</h3><ul><li><a href="/wordpress/blocks/container-block">Layout Blocks</a></li><li><a href="/wordpress/blocks/text-elements-block">Content Blocks</a></li><li><a href="/wordpress/blocks/popup-modals-block">Interactive Blocks</a></li><li><a href="/wordpress/blocks/dynamic-query-block">Dynamic Blocks</a></li></ul></div><div class="docs-section"><h3>Block Controls</h3><ul><li><a href="/wordpress/controls/layout-options">Layout Controls</a></li><li><a href="/wordpress/controls/styles-options">Style Controls</a></li><li><a href="/wordpress/controls/typography-options">Typography Controls</a></li><li><a href="/wordpress/controls/responsive-display">Responsive Controls</a></li></ul></div><div class="docs-section"><h3>Developer Tools</h3><ul><li><a href="/wordpress/dev-tools/custom-post-types">Custom Post Types</a></li><li><a href="/wordpress/dev-tools/custom-taxonomies">Custom Taxonomies</a></li><li><a href="/wordpress/dev-tools/custom-meta-boxes">Meta Boxes</a></li><li><a href="/wordpress/dev-tools/api-creator">API Creator</a></li></ul></div></div><div class="cta-container"><a href="/wordpress/getting-started/installation" class="cta-button primary">Get Started</a><a href="/wordpress/" class="cta-button secondary">Explore Documentation</a><a href="https://github.com/EngCode/phenix-blocks" class="cta-button tertiary">View on GitHub</a></div>',29)]))}const h=t(n,[["render",r]]);export{m as __pageData,h as default};
