import{_ as e,c as o,o as i,ag as a}from"./chunks/framework.CifvWIav.js";const u=JSON.parse('{"title":"Layout Options","description":"","frontmatter":{},"headers":[],"relativePath":"wordpress/blocks-components/control-sets/layout-options.md","filePath":"wordpress/blocks-components/control-sets/layout-options.md"}'),n={name:"wordpress/blocks-components/control-sets/layout-options.md"};function r(l,t,s,d,h,p){return i(),o("div",null,t[0]||(t[0]=[a('<h1 id="layout-options" tabindex="-1">Layout Options <a class="header-anchor" href="#layout-options" aria-label="Permalink to &quot;Layout Options&quot;">​</a></h1><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Layout Options provide powerful controls for structuring and arranging content within blocks. These controls are available across many Phenix blocks and allow you to create sophisticated layouts without writing custom CSS. The Layout Options panel gives you precise control over how elements are positioned, aligned, and displayed.</p><h2 id="key-features" tabindex="-1">Key Features <a class="header-anchor" href="#key-features" aria-label="Permalink to &quot;Key Features&quot;">​</a></h2><ul><li><strong>Responsive layout controls</strong> that adapt to different screen sizes</li><li><strong>Flexbox-based alignment</strong> for precise content positioning</li><li><strong>Width and height controls</strong> for exact sizing</li><li><strong>Display type selection</strong> for changing how elements behave in the layout flow</li><li><strong>Z-index control</strong> for managing element stacking order</li></ul><h2 id="available-controls" tabindex="-1">Available Controls <a class="header-anchor" href="#available-controls" aria-label="Permalink to &quot;Available Controls&quot;">​</a></h2><h3 id="display-type" tabindex="-1">Display Type <a class="header-anchor" href="#display-type" aria-label="Permalink to &quot;Display Type&quot;">​</a></h3><p>Controls how an element behaves in the layout flow.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>Block</td><td>Element takes up the full width of its container and creates a new line</td></tr><tr><td>Inline</td><td>Element only takes up as much width as necessary and doesn&#39;t create a new line</td></tr><tr><td>Inline Block</td><td>Combination of inline and block; doesn&#39;t create a new line but can have width and height</td></tr><tr><td>Flex</td><td>Enables flexbox layout for the element&#39;s children</td></tr><tr><td>Grid</td><td>Enables CSS grid layout for the element&#39;s children</td></tr><tr><td>None</td><td>Removes the element from the normal document flow (often used with absolute positioning)</td></tr></tbody></table><h3 id="width-height" tabindex="-1">Width &amp; Height <a class="header-anchor" href="#width-height" aria-label="Permalink to &quot;Width &amp; Height&quot;">​</a></h3><p>Controls the dimensions of an element.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>Auto</td><td>Element sizes based on its content</td></tr><tr><td>100%</td><td>Element takes full width/height of its parent</td></tr><tr><td>Custom</td><td>Set a specific width/height using px, %, vh/vw, or other CSS units</td></tr><tr><td>Min/Max</td><td>Set minimum or maximum constraints on width/height</td></tr></tbody></table><h3 id="flex-layout-controls" tabindex="-1">Flex Layout Controls <a class="header-anchor" href="#flex-layout-controls" aria-label="Permalink to &quot;Flex Layout Controls&quot;">​</a></h3><p>When Display Type is set to &quot;Flex&quot;, these additional controls become available:</p><table tabindex="0"><thead><tr><th>Control</th><th>Options</th><th>Description</th></tr></thead><tbody><tr><td>Direction</td><td>Row, Row Reverse, Column, Column Reverse</td><td>Sets the direction of flex items</td></tr><tr><td>Wrap</td><td>Nowrap, Wrap, Wrap Reverse</td><td>Controls whether flex items wrap to new lines</td></tr><tr><td>Justify Content</td><td>Start, End, Center, Between, Around, Evenly</td><td>Aligns items along the main axis</td></tr><tr><td>Align Items</td><td>Start, End, Center, Baseline, Stretch</td><td>Aligns items along the cross axis</td></tr><tr><td>Align Content</td><td>Start, End, Center, Between, Around, Stretch</td><td>Aligns multiple lines of content</td></tr><tr><td>Gap</td><td>Custom spacing</td><td>Sets space between flex items</td></tr></tbody></table><h3 id="position-controls" tabindex="-1">Position Controls <a class="header-anchor" href="#position-controls" aria-label="Permalink to &quot;Position Controls&quot;">​</a></h3><p>Controls how an element is positioned relative to its normal position in the document flow.</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>Static</td><td>Default positioning according to normal document flow</td></tr><tr><td>Relative</td><td>Positioned relative to its normal position</td></tr><tr><td>Absolute</td><td>Positioned relative to its nearest positioned ancestor</td></tr><tr><td>Fixed</td><td>Positioned relative to the viewport</td></tr><tr><td>Sticky</td><td>Positioned based on the user&#39;s scroll position</td></tr></tbody></table><h3 id="z-index" tabindex="-1">Z-Index <a class="header-anchor" href="#z-index" aria-label="Permalink to &quot;Z-Index&quot;">​</a></h3><p>Controls the stacking order of elements that overlap. Higher values appear on top of elements with lower values.</p><h2 id="responsive-controls" tabindex="-1">Responsive Controls <a class="header-anchor" href="#responsive-controls" aria-label="Permalink to &quot;Responsive Controls&quot;">​</a></h2><p>All layout options can be set differently for various screen sizes:</p><table tabindex="0"><thead><tr><th>Breakpoint</th><th>Description</th></tr></thead><tbody><tr><td>Default</td><td>Applied to all screen sizes unless overridden</td></tr><tr><td>Mobile</td><td>Applied to screens smaller than 768px</td></tr><tr><td>Tablet</td><td>Applied to screens 768px-991px</td></tr><tr><td>Desktop</td><td>Applied to screens 992px-1199px</td></tr><tr><td>Large Desktop</td><td>Applied to screens 1200px and larger</td></tr></tbody></table><p>To set responsive layout options:</p><ol><li>Select the block you want to modify</li><li>Open the Layout Options panel in the sidebar</li><li>Click the responsive toggle button to switch between breakpoints</li><li>Set the desired layout options for each breakpoint</li></ol><h2 id="usage-examples" tabindex="-1">Usage Examples <a class="header-anchor" href="#usage-examples" aria-label="Permalink to &quot;Usage Examples&quot;">​</a></h2><h3 id="centering-content-vertically-and-horizontally" tabindex="-1">Centering Content Vertically and Horizontally <a class="header-anchor" href="#centering-content-vertically-and-horizontally" aria-label="Permalink to &quot;Centering Content Vertically and Horizontally&quot;">​</a></h3><ol><li>Set Display Type to &quot;Flex&quot;</li><li>Set Justify Content to &quot;Center&quot;</li><li>Set Align Items to &quot;Center&quot;</li></ol><h3 id="creating-a-sticky-header" tabindex="-1">Creating a Sticky Header <a class="header-anchor" href="#creating-a-sticky-header" aria-label="Permalink to &quot;Creating a Sticky Header&quot;">​</a></h3><ol><li>Set Position to &quot;Sticky&quot;</li><li>Set Top to &quot;0&quot;</li><li>Set Z-Index to a high value (e.g., 100)</li></ol><h3 id="creating-a-two-column-layout-on-desktop-single-column-on-mobile" tabindex="-1">Creating a Two-Column Layout on Desktop, Single Column on Mobile <a class="header-anchor" href="#creating-a-two-column-layout-on-desktop-single-column-on-mobile" aria-label="Permalink to &quot;Creating a Two-Column Layout on Desktop, Single Column on Mobile&quot;">​</a></h3><p><strong>Default Settings:</strong></p><ol><li>Set Display Type to &quot;Block&quot;</li></ol><p><strong>Desktop Settings:</strong></p><ol><li>Switch to Desktop breakpoint</li><li>Set Display Type to &quot;Flex&quot;</li><li>Set Direction to &quot;Row&quot;</li></ol><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><ol><li><strong>Start with mobile layouts first</strong>, then adjust for larger screens</li><li><strong>Use flexbox for one-dimensional layouts</strong> (rows or columns)</li><li><strong>Use grid for two-dimensional layouts</strong> (rows and columns together)</li><li><strong>Avoid fixed heights</strong> when possible to maintain content flexibility</li><li><strong>Use percentage widths</strong> for responsive designs rather than fixed pixel values</li><li><strong>Test your layouts</strong> at different screen sizes using the WordPress preview mode</li></ol><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Issue</th><th>Solution</th></tr></thead><tbody><tr><td>Content not centering properly</td><td>Ensure both Justify Content and Align Items are set to &quot;Center&quot;</td></tr><tr><td>Elements overlapping unexpectedly</td><td>Check z-index values and positioning settings</td></tr><tr><td>Layout breaking on mobile</td><td>Set specific layout options for the Mobile breakpoint</td></tr><tr><td>Flex items not wrapping</td><td>Ensure Flex Wrap is set to &quot;Wrap&quot;</td></tr><tr><td>Element not taking full width</td><td>Check if Width is set to &quot;100%&quot;</td></tr></tbody></table><h2 id="related-controls" tabindex="-1">Related Controls <a class="header-anchor" href="#related-controls" aria-label="Permalink to &quot;Related Controls&quot;">​</a></h2><ul><li><a href="./responsive-margin.html">Responsive Margin</a></li><li><a href="./responsive-padding.html">Responsive Padding</a></li><li><a href="./responsive-display.html">Responsive Display</a></li><li><a href="./responsive-position.html">Responsive Position</a></li></ul>',41)]))}const b=e(n,[["render",r]]);export{u as __pageData,b as default};
