import{_ as t,c as i,o,ag as a}from"./chunks/framework.CifvWIav.js";const m=JSON.parse('{"title":"Media Element Block","description":"","frontmatter":{},"headers":[],"relativePath":"wordpress/blocks/media-element-block.md","filePath":"wordpress/blocks/media-element-block.md"}'),r={name:"wordpress/blocks/media-element-block.md"};function l(n,e,d,s,h,c){return o(),i("div",null,e[0]||(e[0]=[a('<h1 id="media-element-block" tabindex="-1">Media Element Block <a class="header-anchor" href="#media-element-block" aria-label="Permalink to &quot;Media Element Block&quot;">​</a></h1><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>The Media Element Block (registered as &quot;Multimedia&quot; in the block inserter) provides a versatile way to add and customize various media types in your content. Based on the Phenix Design System, this block supports images, videos, embeds, and iframes with extensive styling options and responsive behavior.</p><h2 id="key-features" tabindex="-1">Key Features <a class="header-anchor" href="#key-features" aria-label="Permalink to &quot;Key Features&quot;">​</a></h2><ul><li><strong>Multiple Media Types</strong>: Support for images (default), video embeds (YouTube, Vimeo), iframes, and 3D viewers</li><li><strong>Responsive Design</strong>: Comprehensive aspect ratio options (1:1, 16:9, 4:3, etc.) that adapt to different screen sizes</li><li><strong>Lightbox Integration</strong>: Optional lightbox functionality for images and videos when clicked</li><li><strong>Link Capabilities</strong>: Media elements can function as links to other pages or resources</li><li><strong>Styling Options</strong>: Extensive border, shadow, and effect controls through the Phenix Design System</li><li><strong>Inner Block Support</strong>: Ability to nest other blocks within the media container for complex layouts</li><li><strong>Fallback Display</strong>: Automatic placeholder if no media source is provided</li></ul><h2 id="how-to-use" tabindex="-1">How to Use <a class="header-anchor" href="#how-to-use" aria-label="Permalink to &quot;How to Use&quot;">​</a></h2><ol><li><p><strong>Add the Block</strong>: Insert the Media Element block (labeled as &quot;Multimedia&quot;) from the Phenix Blocks category in the block inserter</p></li><li><p><strong>Select Media Type</strong>: From the toolbar dropdown, choose the media type:</p><ul><li>Image (default)</li><li>Video/Embed</li><li>iFrame</li><li>3D Viewer</li></ul></li><li><p><strong>Set Media Source</strong>:</p><ul><li>For images: Upload or select from the media library</li><li>For videos: Enter the video URL or embed code</li><li>For iFrames: Provide the source URL</li></ul></li><li><p><strong>Configure Aspect Ratio</strong>: Select from the predefined aspect ratios in the toolbar to control how the media is displayed</p></li><li><p><strong>Enable Lightbox/Link</strong>: If desired, enable the lightbox feature or set the media as a link to another page</p></li><li><p><strong>Apply Styling</strong>: Use the sidebar panels to customize:</p><ul><li>Border properties (style, width, color, radius)</li><li>Shadow effects</li><li>Position and size</li><li>Responsive behavior</li></ul></li></ol><h2 id="block-settings" tabindex="-1">Block Settings <a class="header-anchor" href="#block-settings" aria-label="Permalink to &quot;Block Settings&quot;">​</a></h2><h3 id="toolbar-controls" tabindex="-1">Toolbar Controls <a class="header-anchor" href="#toolbar-controls" aria-label="Permalink to &quot;Toolbar Controls&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Control</th><th>Description</th></tr></thead><tbody><tr><td>Media Selection</td><td>Upload or select media from the library</td></tr><tr><td>Media Type</td><td>Quick toggle between image, video, audio, or other media types</td></tr><tr><td>Alignment</td><td>Set the media alignment (left, center, right, wide, full)</td></tr><tr><td>Link Controls</td><td>Add or edit links for the media element</td></tr><tr><td>Edit Media</td><td>Access the media editing tools (crop, rotate, etc.)</td></tr><tr><td>Replace</td><td>Replace the current media with another file</td></tr><tr><td>Spacing Controls</td><td>Quick access to margin and padding settings</td></tr><tr><td>Border Options</td><td>Configure border styles, widths, and colors</td></tr></tbody></table><h3 id="sidebar-controls" tabindex="-1">Sidebar Controls <a class="header-anchor" href="#sidebar-controls" aria-label="Permalink to &quot;Sidebar Controls&quot;">​</a></h3><h4 id="media-options-panel" tabindex="-1">Media Options Panel <a class="header-anchor" href="#media-options-panel" aria-label="Permalink to &quot;Media Options Panel&quot;">​</a></h4><table tabindex="0"><thead><tr><th>Control</th><th>Description</th></tr></thead><tbody><tr><td>Media Type</td><td>Choose between image, video, audio, or other media types</td></tr><tr><td>Media Source</td><td>Select from media library, external URL, or embed code</td></tr><tr><td>Media Size</td><td>Select the image size for responsive display</td></tr><tr><td>Alt Text</td><td>Add alternative text for accessibility (images)</td></tr></tbody></table><h4 id="layout-panel" tabindex="-1">Layout Panel <a class="header-anchor" href="#layout-panel" aria-label="Permalink to &quot;Layout Panel&quot;">​</a></h4><table tabindex="0"><thead><tr><th>Control</th><th>Description</th></tr></thead><tbody><tr><td>Alignment</td><td>Set the media alignment (left, center, right)</td></tr><tr><td>Width</td><td>Control the width of the media element</td></tr><tr><td>Height</td><td>Set a specific height or maintain aspect ratio</td></tr><tr><td>Object Fit</td><td>Control how the media fits its container (cover, contain, fill)</td></tr><tr><td>Object Position</td><td>Set the focus point for cropped media</td></tr></tbody></table><h4 id="style-panel" tabindex="-1">Style Panel <a class="header-anchor" href="#style-panel" aria-label="Permalink to &quot;Style Panel&quot;">​</a></h4><table tabindex="0"><thead><tr><th>Control</th><th>Description</th></tr></thead><tbody><tr><td>Border</td><td>Add borders with custom color, width, and style</td></tr><tr><td>Border Radius</td><td>Add rounded corners or create circular images</td></tr><tr><td>Shadow</td><td>Add shadow effects to the media element</td></tr><tr><td>Opacity</td><td>Control the transparency of the media</td></tr><tr><td>Filters</td><td>Apply CSS filters (grayscale, blur, contrast, etc.)</td></tr><tr><td>Mask</td><td>Apply shape masks to images</td></tr></tbody></table><h4 id="link-panel" tabindex="-1">Link Panel <a class="header-anchor" href="#link-panel" aria-label="Permalink to &quot;Link Panel&quot;">​</a></h4><table tabindex="0"><thead><tr><th>Control</th><th>Description</th></tr></thead><tbody><tr><td>Link To</td><td>Set where the media links to (none, media file, custom URL)</td></tr><tr><td>Open in New Tab</td><td>Whether the link opens in a new browser tab</td></tr><tr><td>Lightbox</td><td>Enable lightbox view for images</td></tr><tr><td>Download</td><td>Allow users to download the media</td></tr></tbody></table><h4 id="video-audio-panel" tabindex="-1">Video/Audio Panel <a class="header-anchor" href="#video-audio-panel" aria-label="Permalink to &quot;Video/Audio Panel&quot;">​</a></h4><table tabindex="0"><thead><tr><th>Control</th><th>Description</th></tr></thead><tbody><tr><td>Autoplay</td><td>Automatically start playback when page loads</td></tr><tr><td>Loop</td><td>Continuously repeat the media</td></tr><tr><td>Controls</td><td>Show or hide player controls</td></tr><tr><td>Muted</td><td>Start with sound muted</td></tr><tr><td>Poster</td><td>Set a thumbnail image for video (before play)</td></tr></tbody></table><h2 id="implementation-details" tabindex="-1">Implementation Details <a class="header-anchor" href="#implementation-details" aria-label="Permalink to &quot;Implementation Details&quot;">​</a></h2><p>The Media Element block is implemented with a combination of JavaScript for the editor interface and client-side rendering for the frontend display.</p><h3 id="media-types" tabindex="-1">Media Types <a class="header-anchor" href="#media-types" aria-label="Permalink to &quot;Media Types&quot;">​</a></h3><p>The block supports the following media types, each with specific rendering behavior:</p><ol><li><p><strong>Image</strong> (default)</p><ul><li>Renders as an <code>&lt;img&gt;</code> element with responsive classes</li><li>Supports alt text, border styling, and lightbox functionality</li><li>Default fallback to placeholder if no source is provided</li></ul></li><li><p><strong>Video/Embed</strong></p><ul><li>Supports multiple embed types: direct video, YouTube, Vimeo, and others</li><li>Rendered with appropriate aspect ratio container</li><li>Can include player controls, autoplay, and mute options</li></ul></li><li><p><strong>iFrame</strong></p><ul><li>Allows embedding external content in a responsive container</li><li>Supports custom sizing and aspect ratios</li></ul></li><li><p><strong>3D Viewer</strong></p><ul><li>Specialized container for 3D model viewing</li></ul></li></ol><h3 id="aspect-ratios" tabindex="-1">Aspect Ratios <a class="header-anchor" href="#aspect-ratios" aria-label="Permalink to &quot;Aspect Ratios&quot;">​</a></h3><p>The block provides numerous predefined aspect ratios that can be applied to media elements:</p><ul><li>None (natural dimensions)</li><li>1:1 (square)</li><li>16:9 (widescreen video)</li><li>4:3 (standard video)</li><li>3:4 (portrait)</li><li>16:10 (widescreen monitor)</li><li>21:9 (ultrawide)</li><li>And many other options</li></ul><h3 id="interactive-features" tabindex="-1">Interactive Features <a class="header-anchor" href="#interactive-features" aria-label="Permalink to &quot;Interactive Features&quot;">​</a></h3><ul><li><strong>Lightbox Support</strong>: When enabled, clicking on the media opens it in a lightbox overlay</li><li><strong>Link Functionality</strong>: Media can be wrapped in links to navigate to other pages or resources</li><li><strong>Inner Blocks</strong>: Supports nesting other blocks within the media container for complex layouts</li></ul><h2 id="technical-implementation" tabindex="-1">Technical Implementation <a class="header-anchor" href="#technical-implementation" aria-label="Permalink to &quot;Technical Implementation&quot;">​</a></h2><p>The Media Element block is built using the following components:</p><ol><li><p><strong>Block Registration</strong>: Registered with the WordPress block API with specific attributes for media type, source, and styling options</p></li><li><p><strong>Editor Interface</strong>: Implemented with React components for the block editor, providing toolbar controls and sidebar panels</p></li><li><p><strong>Rendering Logic</strong>:</p><ul><li>Client-side rendering using the <code>save</code> method for static content</li><li>Dynamic class generation based on selected attributes</li><li>Conditional rendering based on media type</li></ul></li><li><p><strong>Style System</strong>:</p><ul><li>Utilizes the Phenix Design System&#39;s utility classes for styling</li><li>Supports responsive design through breakpoint-specific classes</li></ul></li></ol><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><ol><li><p><strong>Optimize Media Files</strong>: Compress images and videos before uploading to improve page load times</p></li><li><p><strong>Use Alt Text</strong>: Always add descriptive alternative text for images to improve accessibility</p></li><li><p><strong>Responsive Sizing</strong>: Use responsive size options rather than fixed dimensions when possible</p></li><li><p><strong>Aspect Ratio</strong>: Maintain consistent aspect ratios for media elements, especially in grids or galleries</p></li><li><p><strong>Lazy Loading</strong>: Enable lazy loading for media-heavy pages to improve performance</p></li><li><p><strong>Video Considerations</strong>: Set video elements to not autoplay and start muted to improve user experience</p></li></ol><h2 id="related-blocks" tabindex="-1">Related Blocks <a class="header-anchor" href="#related-blocks" aria-label="Permalink to &quot;Related Blocks&quot;">​</a></h2><ul><li><a href="./container-block.html">Container Block</a> - For positioning media within layouts</li><li><a href="./grid-row-block.html">Grid Row Block</a> - For creating media galleries with grid layouts</li><li><a href="./group-block.html">Group Block</a> - For combining media with other content elements</li></ul>',38)]))}const u=t(r,[["render",l]]);export{m as __pageData,u as default};
