import{_ as e,c as o,o as r,ag as a}from"./chunks/framework.DPDPlp3K.js";const u=JSON.parse('{"title":"Styles Options","description":"","frontmatter":{},"headers":[],"relativePath":"wordpress/controls/styles-options.md","filePath":"wordpress/controls/styles-options.md"}'),i={name:"wordpress/controls/styles-options.md"};function l(s,t,n,d,h,c){return r(),o("div",null,t[0]||(t[0]=[a('<h1 id="styles-options" tabindex="-1">Styles Options <a class="header-anchor" href="#styles-options" aria-label="Permalink to &quot;Styles Options&quot;">​</a></h1><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>The Styles Options control in the Phenix Design System provides a comprehensive set of visual styling tools for blocks and elements. This powerful feature allows you to customize backgrounds, borders, shadows, and other visual properties without writing custom CSS. With responsive options for different screen sizes, you can create visually appealing designs that adapt seamlessly across devices.</p><h2 id="key-features" tabindex="-1">Key Features <a class="header-anchor" href="#key-features" aria-label="Permalink to &quot;Key Features&quot;">​</a></h2><ul><li><strong>Background controls</strong> for colors, gradients, and images</li><li><strong>Border styling</strong> with width, style, and color options</li><li><strong>Shadow effects</strong> for depth and dimension</li><li><strong>Opacity settings</strong> for transparency control</li><li><strong>Transform properties</strong> for rotation, scaling, and more</li><li><strong>Transition effects</strong> for smooth animations</li><li><strong>Responsive settings</strong> for different screen sizes</li></ul><h2 id="available-controls" tabindex="-1">Available Controls <a class="header-anchor" href="#available-controls" aria-label="Permalink to &quot;Available Controls&quot;">​</a></h2><h3 id="background-controls" tabindex="-1">Background Controls <a class="header-anchor" href="#background-controls" aria-label="Permalink to &quot;Background Controls&quot;">​</a></h3><p>Customize the background appearance of elements:</p><h4 id="background-color" tabindex="-1">Background Color <a class="header-anchor" href="#background-color" aria-label="Permalink to &quot;Background Color&quot;">​</a></h4><ul><li>Select from theme color palette</li><li>Choose custom colors with opacity/transparency</li><li>Apply solid colors or gradients</li></ul><h4 id="background-gradient" tabindex="-1">Background Gradient <a class="header-anchor" href="#background-gradient" aria-label="Permalink to &quot;Background Gradient&quot;">​</a></h4><ul><li>Linear or radial gradient types</li><li>Multiple color stops with position control</li><li>Adjustable gradient angle or position</li></ul><h4 id="background-image" tabindex="-1">Background Image <a class="header-anchor" href="#background-image" aria-label="Permalink to &quot;Background Image&quot;">​</a></h4><ul><li>Upload or select images from media library</li><li>Control image positioning (center, top, bottom, etc.)</li><li>Set image size (cover, contain, custom)</li><li>Control image repetition (repeat, no-repeat, etc.)</li><li>Add parallax effects for depth</li></ul><h3 id="border-controls" tabindex="-1">Border Controls <a class="header-anchor" href="#border-controls" aria-label="Permalink to &quot;Border Controls&quot;">​</a></h3><p>Add and customize borders around elements:</p><h4 id="border-width" tabindex="-1">Border Width <a class="header-anchor" href="#border-width" aria-label="Permalink to &quot;Border Width&quot;">​</a></h4><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>None</td><td>No border</td></tr><tr><td>Thin</td><td>1px border width</td></tr><tr><td>Medium</td><td>2px border width</td></tr><tr><td>Thick</td><td>4px border width</td></tr><tr><td>Custom</td><td>Custom width in px, rem, or other units</td></tr></tbody></table><h4 id="border-style" tabindex="-1">Border Style <a class="header-anchor" href="#border-style" aria-label="Permalink to &quot;Border Style&quot;">​</a></h4><table tabindex="0"><thead><tr><th>Style</th><th>Description</th></tr></thead><tbody><tr><td>Solid</td><td>Continuous solid line</td></tr><tr><td>Dashed</td><td>Series of dashes</td></tr><tr><td>Dotted</td><td>Series of dots</td></tr><tr><td>Double</td><td>Two parallel solid lines</td></tr><tr><td>Groove</td><td>3D groove effect</td></tr><tr><td>Ridge</td><td>3D ridge effect</td></tr></tbody></table><h4 id="border-color" tabindex="-1">Border Color <a class="header-anchor" href="#border-color" aria-label="Permalink to &quot;Border Color&quot;">​</a></h4><ul><li>Select from theme color palette</li><li>Choose custom colors with opacity/transparency</li></ul><h4 id="border-direction" tabindex="-1">Border Direction <a class="header-anchor" href="#border-direction" aria-label="Permalink to &quot;Border Direction&quot;">​</a></h4><table tabindex="0"><thead><tr><th>Direction</th><th>Description</th></tr></thead><tbody><tr><td>All Sides</td><td>Apply border to all sides</td></tr><tr><td>Top</td><td>Apply border only to the top</td></tr><tr><td>Right</td><td>Apply border only to the right</td></tr><tr><td>Bottom</td><td>Apply border only to the bottom</td></tr><tr><td>Left</td><td>Apply border only to the left</td></tr><tr><td>Horizontal</td><td>Apply border to top and bottom</td></tr><tr><td>Vertical</td><td>Apply border to left and right</td></tr></tbody></table><h3 id="shadow-effects" tabindex="-1">Shadow Effects <a class="header-anchor" href="#shadow-effects" aria-label="Permalink to &quot;Shadow Effects&quot;">​</a></h3><p>Add depth and dimension with shadow effects:</p><h4 id="box-shadow" tabindex="-1">Box Shadow <a class="header-anchor" href="#box-shadow" aria-label="Permalink to &quot;Box Shadow&quot;">​</a></h4><ul><li>Horizontal Offset: Control shadow position left/right</li><li>Vertical Offset: Control shadow position up/down</li><li>Blur Radius: Control shadow softness</li><li>Spread Radius: Control shadow size</li><li>Shadow Color: Select color with opacity</li><li>Inset Option: Create inner shadow effect</li></ul><h4 id="shadow-presets" tabindex="-1">Shadow Presets <a class="header-anchor" href="#shadow-presets" aria-label="Permalink to &quot;Shadow Presets&quot;">​</a></h4><table tabindex="0"><thead><tr><th>Preset</th><th>Description</th></tr></thead><tbody><tr><td>None</td><td>No shadow</td></tr><tr><td>Small</td><td>Subtle shadow effect</td></tr><tr><td>Medium</td><td>Moderate shadow effect</td></tr><tr><td>Large</td><td>Pronounced shadow effect</td></tr><tr><td>Extra Large</td><td>Maximum shadow effect</td></tr></tbody></table><h3 id="opacity-control" tabindex="-1">Opacity Control <a class="header-anchor" href="#opacity-control" aria-label="Permalink to &quot;Opacity Control&quot;">​</a></h3><p>Adjust the transparency of elements:</p><ul><li>Range from 0 (completely transparent) to 1 (fully opaque)</li><li>Precise control with decimal values (e.g., 0.75)</li></ul><h3 id="transform-properties" tabindex="-1">Transform Properties <a class="header-anchor" href="#transform-properties" aria-label="Permalink to &quot;Transform Properties&quot;">​</a></h3><p>Apply visual transformations to elements:</p><h4 id="rotate" tabindex="-1">Rotate <a class="header-anchor" href="#rotate" aria-label="Permalink to &quot;Rotate&quot;">​</a></h4><ul><li>Rotate elements from 0 to 360 degrees</li><li>Positive values for clockwise rotation</li><li>Negative values for counter-clockwise rotation</li></ul><h4 id="scale" tabindex="-1">Scale <a class="header-anchor" href="#scale" aria-label="Permalink to &quot;Scale&quot;">​</a></h4><ul><li>Scale elements larger or smaller</li><li>Values greater than 1 increase size</li><li>Values less than 1 decrease size</li><li>Control X and Y scaling independently</li></ul><h4 id="translate" tabindex="-1">Translate <a class="header-anchor" href="#translate" aria-label="Permalink to &quot;Translate&quot;">​</a></h4><ul><li>Move elements horizontally (X) and vertically (Y)</li><li>Use pixel, percentage, or other CSS units</li><li>Positive/negative values for different directions</li></ul><h4 id="skew" tabindex="-1">Skew <a class="header-anchor" href="#skew" aria-label="Permalink to &quot;Skew&quot;">​</a></h4><ul><li>Apply angular distortion to elements</li><li>Control X and Y skew independently</li><li>Values in degrees (e.g., 15deg, -10deg)</li></ul><h3 id="transition-effects" tabindex="-1">Transition Effects <a class="header-anchor" href="#transition-effects" aria-label="Permalink to &quot;Transition Effects&quot;">​</a></h3><p>Create smooth animations between states:</p><ul><li>Transition Property: What properties to animate</li><li>Duration: How long the transition takes</li><li>Timing Function: How the transition progresses over time</li><li>Delay: Time before the transition starts</li></ul><h2 id="responsive-controls" tabindex="-1">Responsive Controls <a class="header-anchor" href="#responsive-controls" aria-label="Permalink to &quot;Responsive Controls&quot;">​</a></h2><p>All style options can be set differently for various screen sizes:</p><table tabindex="0"><thead><tr><th>Breakpoint</th><th>Description</th></tr></thead><tbody><tr><td>Default</td><td>Applied to all screen sizes unless overridden</td></tr><tr><td>Mobile</td><td>Applied to screens smaller than 768px</td></tr><tr><td>Tablet</td><td>Applied to screens 768px-991px</td></tr><tr><td>Desktop</td><td>Applied to screens 992px-1199px</td></tr><tr><td>Large Desktop</td><td>Applied to screens 1200px and larger</td></tr></tbody></table><p>To set responsive styles:</p><ol><li>Select the block you want to modify</li><li>Open the Styles Options panel in the sidebar</li><li>Click the responsive toggle button to switch between breakpoints</li><li>Set the desired style options for each breakpoint</li></ol><h2 id="usage-examples" tabindex="-1">Usage Examples <a class="header-anchor" href="#usage-examples" aria-label="Permalink to &quot;Usage Examples&quot;">​</a></h2><h3 id="creating-a-card-with-shadow-and-border" tabindex="-1">Creating a Card with Shadow and Border <a class="header-anchor" href="#creating-a-card-with-shadow-and-border" aria-label="Permalink to &quot;Creating a Card with Shadow and Border&quot;">​</a></h3><ol><li>Select a Group or Container block</li><li>Open the Styles Options panel</li><li>Set Background Color to white or a light color</li><li>Add a Medium Box Shadow</li><li>Set Border to Thin, Solid, with a light gray color</li><li>Set Border Radius to MD (0.5rem/8px) using the Border Radius control</li></ol><h3 id="creating-a-gradient-background-section" tabindex="-1">Creating a Gradient Background Section <a class="header-anchor" href="#creating-a-gradient-background-section" aria-label="Permalink to &quot;Creating a Gradient Background Section&quot;">​</a></h3><ol><li>Select a Container block</li><li>Open the Styles Options panel</li><li>Set Background Type to Gradient</li><li>Choose two complementary colors</li><li>Set Gradient Type to Linear</li><li>Set Gradient Angle to 135 degrees</li></ol><h3 id="creating-a-hero-section-with-background-image" tabindex="-1">Creating a Hero Section with Background Image <a class="header-anchor" href="#creating-a-hero-section-with-background-image" aria-label="Permalink to &quot;Creating a Hero Section with Background Image&quot;">​</a></h3><ol><li>Select a Container block</li><li>Open the Styles Options panel</li><li>Set Background Type to Image</li><li>Upload or select an image</li><li>Set Background Size to Cover</li><li>Set Background Position to Center Center</li><li>Add a semi-transparent overlay color if needed</li></ol><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><ol><li><p><strong>Maintain consistency</strong>: Use the same style patterns throughout your site</p></li><li><p><strong>Consider performance</strong>: Large background images can affect page load times</p></li><li><p><strong>Use shadows sparingly</strong>: Too many shadow effects can create visual noise</p></li><li><p><strong>Test on different devices</strong>: Ensure your styles look good across all screen sizes</p></li><li><p><strong>Combine with other controls</strong>: Use together with Layout Options, Responsive Margin, and Padding for comprehensive design</p></li><li><p><strong>Consider accessibility</strong>: Ensure sufficient contrast between text and backgrounds</p></li><li><p><strong>Use theme colors</strong>: Stick to your theme&#39;s color palette for a cohesive look</p></li></ol><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Issue</th><th>Solution</th></tr></thead><tbody><tr><td>Background image not showing</td><td>Check image URL and ensure it&#39;s properly uploaded</td></tr><tr><td>Shadow not visible</td><td>Increase shadow opacity or size, or check if it&#39;s hidden by another element</td></tr><tr><td>Gradient not appearing</td><td>Ensure you&#39;ve selected different colors for the gradient stops</td></tr><tr><td>Styles not applying on mobile</td><td>Check if you have conflicting styles set for the mobile breakpoint</td></tr><tr><td>Border only showing on some sides</td><td>Verify your border direction settings</td></tr></tbody></table><h2 id="related-controls" tabindex="-1">Related Controls <a class="header-anchor" href="#related-controls" aria-label="Permalink to &quot;Related Controls&quot;">​</a></h2><ul><li><a href="./border-radius.html">Border Radius</a></li><li><a href="./layout-options.html">Layout Options</a></li><li><a href="./responsive-margin.html">Responsive Margin</a></li><li><a href="./responsive-padding.html">Responsive Padding</a></li><li><a href="./typography-options.html">Typography Options</a></li></ul>',64)]))}const g=e(i,[["render",l]]);export{u as __pageData,g as default};
