import{_ as e,c as i,o as a,ag as o}from"./chunks/framework.B2cFWCz1.js";const u=JSON.parse('{"title":"Carousel Slider","description":"","frontmatter":{},"headers":[],"relativePath":"wordpress/controls/swiper-slider.md","filePath":"wordpress/controls/swiper-slider.md"}'),r={name:"wordpress/controls/swiper-slider.md"};function l(s,t,d,n,c,h){return a(),i("div",null,t[0]||(t[0]=[o('<h1 id="carousel-slider" tabindex="-1">Carousel Slider <a class="header-anchor" href="#carousel-slider" aria-label="Permalink to &quot;Carousel Slider&quot;">​</a></h1><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>The Carousel Slider control in the Phenix Design System provides powerful functionality for creating interactive slideshows and carousels. Built on the robust Swiper.js library, this feature allows you to showcase multiple content items in a space-efficient, engaging format. With comprehensive configuration options and responsive settings, you can create everything from simple image sliders to complex product carousels and testimonial displays.</p><h2 id="key-features" tabindex="-1">Key Features <a class="header-anchor" href="#key-features" aria-label="Permalink to &quot;Key Features&quot;">​</a></h2><ul><li><strong>Touch-enabled navigation</strong> for mobile and desktop</li><li><strong>Multiple slide layouts</strong> including grid and stack options</li><li><strong>Autoplay functionality</strong> with customizable timing</li><li><strong>Navigation controls</strong> including arrows, dots, and thumbnails</li><li><strong>Transition effects</strong> for engaging slide changes</li><li><strong>Responsive settings</strong> for different screen sizes</li><li><strong>Accessibility features</strong> for inclusive user experience</li></ul><h2 id="how-carousel-slider-works" tabindex="-1">How Carousel Slider Works <a class="header-anchor" href="#how-carousel-slider-works" aria-label="Permalink to &quot;How Carousel Slider Works&quot;">​</a></h2><p>The Carousel Slider transforms a collection of content blocks into an interactive slideshow. It can be applied to various blocks in the Phenix Design System, including the Slider Block, Dynamic Query Block, and others that support carousel functionality.</p><h2 id="available-controls" tabindex="-1">Available Controls <a class="header-anchor" href="#available-controls" aria-label="Permalink to &quot;Available Controls&quot;">​</a></h2><h3 id="slider-layout" tabindex="-1">Slider Layout <a class="header-anchor" href="#slider-layout" aria-label="Permalink to &quot;Slider Layout&quot;">​</a></h3><p>Control how slides are arranged and displayed:</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>Single</td><td>One slide visible at a time</td></tr><tr><td>Multiple</td><td>Multiple slides visible at once</td></tr><tr><td>Grid</td><td>Slides arranged in rows and columns</td></tr><tr><td>Stack</td><td>Slides partially overlapping each other</td></tr></tbody></table><h3 id="slides-per-view" tabindex="-1">Slides Per View <a class="header-anchor" href="#slides-per-view" aria-label="Permalink to &quot;Slides Per View&quot;">​</a></h3><p>Set how many slides are visible simultaneously:</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>1</td><td>One slide at a time (default)</td></tr><tr><td>2-6</td><td>Multiple slides visible at once</td></tr><tr><td>Auto</td><td>Automatically calculate based on slide width</td></tr></tbody></table><h3 id="space-between-slides" tabindex="-1">Space Between Slides <a class="header-anchor" href="#space-between-slides" aria-label="Permalink to &quot;Space Between Slides&quot;">​</a></h3><p>Control the gap between slides:</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>None</td><td>No gap between slides</td></tr><tr><td>Small</td><td>8px gap between slides</td></tr><tr><td>Medium</td><td>16px gap between slides</td></tr><tr><td>Large</td><td>24px gap between slides</td></tr><tr><td>Custom</td><td>Set a custom gap value</td></tr></tbody></table><h3 id="navigation-controls" tabindex="-1">Navigation Controls <a class="header-anchor" href="#navigation-controls" aria-label="Permalink to &quot;Navigation Controls&quot;">​</a></h3><p>Choose which navigation elements to display:</p><table tabindex="0"><thead><tr><th>Control</th><th>Description</th></tr></thead><tbody><tr><td>Arrows</td><td>Previous/Next buttons for slide navigation</td></tr><tr><td>Dots</td><td>Pagination indicators showing slide count and current position</td></tr><tr><td>Thumbnails</td><td>Small preview images for direct slide access</td></tr><tr><td>None</td><td>No visible navigation controls</td></tr></tbody></table><h3 id="autoplay-settings" tabindex="-1">Autoplay Settings <a class="header-anchor" href="#autoplay-settings" aria-label="Permalink to &quot;Autoplay Settings&quot;">​</a></h3><p>Configure automatic slide advancement:</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>Enable/Disable</td><td>Turn autoplay on or off</td></tr><tr><td>Delay</td><td>Time between slide changes (in milliseconds)</td></tr><tr><td>Pause on Hover</td><td>Stop autoplay when user hovers over slider</td></tr><tr><td>Reverse Direction</td><td>Play slides in reverse order</td></tr></tbody></table><h3 id="transition-effects" tabindex="-1">Transition Effects <a class="header-anchor" href="#transition-effects" aria-label="Permalink to &quot;Transition Effects&quot;">​</a></h3><p>Choose how slides transition from one to another:</p><table tabindex="0"><thead><tr><th>Effect</th><th>Description</th></tr></thead><tbody><tr><td>Slide</td><td>Standard horizontal slide transition</td></tr><tr><td>Fade</td><td>Smooth fade between slides</td></tr><tr><td>Cube</td><td>3D cube rotation effect</td></tr><tr><td>Coverflow</td><td>3D flip effect with depth</td></tr><tr><td>Flip</td><td>Vertical or horizontal flip transition</td></tr><tr><td>Cards</td><td>Card-style transition effect</td></tr></tbody></table><h3 id="loop-and-rewind" tabindex="-1">Loop and Rewind <a class="header-anchor" href="#loop-and-rewind" aria-label="Permalink to &quot;Loop and Rewind&quot;">​</a></h3><p>Control slide behavior at the beginning and end:</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>Loop</td><td>Continuous looping from last slide back to first</td></tr><tr><td>Rewind</td><td>Jump back to first slide after reaching the end</td></tr><tr><td>Stop</td><td>Stop at the last slide</td></tr></tbody></table><h3 id="advanced-options" tabindex="-1">Advanced Options <a class="header-anchor" href="#advanced-options" aria-label="Permalink to &quot;Advanced Options&quot;">​</a></h3><p>Fine-tune slider behavior with advanced settings:</p><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>Free Mode</td><td>Slides move freely like a scroller rather than snapping</td></tr><tr><td>Centered Slides</td><td>Active slide is centered in the view</td></tr><tr><td>Initial Slide</td><td>Which slide to show first (0-based index)</td></tr><tr><td>Speed</td><td>Transition speed in milliseconds</td></tr><tr><td>Direction</td><td>Horizontal or vertical slider orientation</td></tr><tr><td>Effect Speed</td><td>Duration of transition effects</td></tr><tr><td>Mousewheel Control</td><td>Enable/disable mousewheel navigation</td></tr><tr><td>Keyboard Control</td><td>Enable/disable keyboard navigation</td></tr></tbody></table><h2 id="responsive-controls" tabindex="-1">Responsive Controls <a class="header-anchor" href="#responsive-controls" aria-label="Permalink to &quot;Responsive Controls&quot;">​</a></h2><p>The Carousel Slider control allows you to set different slider configurations for various screen sizes:</p><table tabindex="0"><thead><tr><th>Breakpoint</th><th>Description</th></tr></thead><tbody><tr><td>Default</td><td>Applied to all screen sizes unless overridden</td></tr><tr><td>Mobile</td><td>Applied to screens smaller than 768px</td></tr><tr><td>Tablet</td><td>Applied to screens 768px-991px</td></tr><tr><td>Desktop</td><td>Applied to screens 992px-1199px</td></tr><tr><td>Large Desktop</td><td>Applied to screens 1200px and larger</td></tr></tbody></table><p>To set responsive slider options:</p><ol><li>Select the block with carousel functionality</li><li>Open the Carousel Slider panel in the sidebar</li><li>Click the responsive toggle button to switch between breakpoints</li><li>Set the desired slider options for each breakpoint</li></ol><h2 id="usage-examples" tabindex="-1">Usage Examples <a class="header-anchor" href="#usage-examples" aria-label="Permalink to &quot;Usage Examples&quot;">​</a></h2><h3 id="creating-an-image-slider" tabindex="-1">Creating an Image Slider <a class="header-anchor" href="#creating-an-image-slider" aria-label="Permalink to &quot;Creating an Image Slider&quot;">​</a></h3><ol><li>Add a Slider Block to your page</li><li>Add Media Element blocks for each slide</li><li>Open the Carousel Slider panel</li><li>Configure basic settings: <ul><li>Slides Per View: 1</li><li>Navigation: Arrows and Dots</li><li>Transition Effect: Slide</li><li>Enable Autoplay with 5000ms delay</li></ul></li></ol><h3 id="creating-a-product-carousel" tabindex="-1">Creating a Product Carousel <a class="header-anchor" href="#creating-a-product-carousel" aria-label="Permalink to &quot;Creating a Product Carousel&quot;">​</a></h3><ol><li>Add a Dynamic Query Block to your page</li><li>Set it to query Products (or custom post type)</li><li>Open the Carousel Slider panel</li><li>Configure settings: <ul><li>Slides Per View: 3 (Desktop), 2 (Tablet), 1 (Mobile)</li><li>Space Between: Medium</li><li>Navigation: Arrows only</li><li>Loop: Enabled</li></ul></li></ol><h3 id="creating-a-testimonial-slider" tabindex="-1">Creating a Testimonial Slider <a class="header-anchor" href="#creating-a-testimonial-slider" aria-label="Permalink to &quot;Creating a Testimonial Slider&quot;">​</a></h3><ol><li>Add a Slider Block to your page</li><li>Add Group blocks for each testimonial, containing text and image</li><li>Open the Carousel Slider panel</li><li>Configure settings: <ul><li>Transition Effect: Fade</li><li>Navigation: Dots only</li><li>Autoplay: Enabled with 7000ms delay</li><li>Pause on Hover: Enabled</li></ul></li></ol><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><ol><li><p><strong>Optimize for mobile</strong>: Ensure your slider works well on small screens</p></li><li><p><strong>Limit slides per view</strong>: Show fewer slides on mobile than on desktop</p></li><li><p><strong>Optimize images</strong>: Use appropriately sized and compressed images</p></li><li><p><strong>Keep transitions simple</strong>: Fancy effects can be distracting and may affect performance</p></li><li><p><strong>Include navigation</strong>: Always provide a way for users to navigate manually</p></li><li><p><strong>Consider load time</strong>: Too many slides can impact page performance</p></li><li><p><strong>Test thoroughly</strong>: Check your slider on different devices and browsers</p></li></ol><h2 id="accessibility-considerations" tabindex="-1">Accessibility Considerations <a class="header-anchor" href="#accessibility-considerations" aria-label="Permalink to &quot;Accessibility Considerations&quot;">​</a></h2><ol><li><p><strong>Keyboard navigation</strong>: Ensure users can navigate with keyboard</p></li><li><p><strong>Pause on focus</strong>: Autoplay should pause when a user focuses on the slider</p></li><li><p><strong>Sufficient contrast</strong>: Navigation controls should have good contrast</p></li><li><p><strong>Text alternatives</strong>: Include alt text for all images in slides</p></li><li><p><strong>Reduced motion</strong>: Consider users who prefer reduced motion</p></li></ol><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Issue</th><th>Solution</th></tr></thead><tbody><tr><td>Slider not advancing</td><td>Check if autoplay is enabled and delay is set correctly</td></tr><tr><td>Navigation controls not working</td><td>Verify that navigation options are enabled</td></tr><tr><td>Slides stacking incorrectly</td><td>Check slides per view and space between settings</td></tr><tr><td>Transition effects not showing</td><td>Ensure transition effect is supported by your browser</td></tr><tr><td>Performance issues</td><td>Reduce number of slides, optimize images, simplify transitions</td></tr><tr><td>Touch not working on mobile</td><td>Check if touch is enabled in slider settings</td></tr></tbody></table><h2 id="related-controls-and-blocks" tabindex="-1">Related Controls and Blocks <a class="header-anchor" href="#related-controls-and-blocks" aria-label="Permalink to &quot;Related Controls and Blocks&quot;">​</a></h2><ul><li><a href="./../blocks/slider-block.html">Slider Block</a></li><li><a href="./../blocks/dynamic-query-block.html">Dynamic Query Block</a></li><li><a href="./responsive-display.html">Responsive Display</a></li><li><a href="./layout-options.html">Layout Options</a></li></ul>',52)]))}const b=e(r,[["render",l]]);export{u as __pageData,b as default};
